sourcesContent:
import sql from "mssql"
import dbConfig from "../dbConfig";

class ArticleComments{
    constructor(id, content, score, timeStamp, userId, articleId, parentCommentId, level=null){
        this.commentId = id;
        this.content = content;
        this.score = score;
        this.timeStamp = timeStamp;
        this.userId = userId;
        this.articleId = articleId;
        this.parentCommentId = parentCommentId;
        this.level = level;
    }

    static async getAllCommentsFromArticleId(articleId){
        let connection;
        
        try{
            const connection = await sql.connect(dbConfig);
            const sqlQuery = `                
                WITH CommentTree AS (
                    SELECT
                        commentId,
                        content,
                        parentCommentId,
                        timeStamp,
                        score,
                        userId,
                        articleId,
                        0 AS level 
                    FROM ArticleComments
                    WHERE parentCommentId IS NULL AND articleId = @articleId

                    UNION ALL

                    SELECT
                        ac.commentId,
                        ac.content,
                        ac.parentCommentId,
                        ac.timeStamp,
                        ac.score,
                        ac.userId,
                        ac.articleId,
                        ct.level + 1
                    FROM ArticleComments ac
                    INNER JOIN CommentTree ct ON ac.parentCommentId = ct.commentId
                )
                SELECT * FROM CommentTree
                ORDER BY level, timeStamp
                OPTION (MAXRECURSION 0);
            `;
            // MIGHT NEED TO CHANGE SQL QUERY

            const request = connection.request();
            request.input("articleId", sql.Int, articleId);
            const result = await request.query(sqlQuery);
            
            // Map the recordset to an array of ArticleComments objects
            const comments = result.recordset.map(record => 
                new ArticleComments(
                    record.commentId,
                    record.content,
                    record.score,
                    record.timeStamp,
                    record.userId,
                    record.articleId,
                    record.parentCommentId,
                    record.level
                )
            );
            return comments;
        } catch (error) {
            console.error('Error getting comments:', error);
            throw new Error("Error getting comments");
