//         const token = jwt.sign({ id: user.userId, userType: user.userType }, 'your_jwt_secret', { expiresIn: '1h' });
//         res.json({ token, userType: user.userType });
//     } catch (err) {
//         console.error(err);
//         res.status(500).send("Server error");
//     }
// };

module.exports = {
  getAllUsers,
  getUserByUserId,
  createUser,
  updateUser,
  deleteUser,
  deleteUserById,
  loginUser,
  getUserProfileByUserId,
  fetchProfilePicture,
  uploadProfilePicture,
};

