sourcesContent:
import sql from "mssql";
import dbConfig from "../dbConfig";
const EventAttendanceModel = require('./eventAttendanceModel'); 

class EventModel {
    constructor(eventId, eventName, eventDesc, eventOverview, eventCategory, eventReports, eventTime, creatorId, creatorName, cost, eventImage) {
        this.eventId = eventId;
        this.eventName = eventName;
        this.eventDesc = eventDesc;
        this.eventOverview = eventOverview;
        this.eventCategory = eventCategory;
        this.eventReports = eventReports;
        this.eventTime = eventTime;
        this.creatorId = creatorId;
        this.creatorName = creatorName;
        this.cost = cost;
        this.eventImage = eventImage;
    }

    static async getAllEvents() {
        let connection;

        try {
            connection = await sql.connect(dbConfig);
            const sqlQuery = `
                SELECT 
                    e.eventId, 
                    e.eventName, 
                    e.eventDesc, 
                    e.eventOverview, 
                    e.eventCategory, 
                    e.eventReports, 
                    e.eventTime, 
                    e.creatorId, 
                    u.name AS creatorName,
                    e.cost,
                    e.eventImage
                FROM Events e
                JOIN Users u ON e.creatorId = u.userId
            `;
            const request = connection.request(); 
            const result = await request.query(sqlQuery);

            const events = result.recordset.map(record => {
                // Convert binary image data to base64 string
                const eventImageBase64 = record.eventImage ? record.eventImage.toString('base64') : null;
                return new EventModel(
                    record.eventId,
                    record.eventName,
                    record.eventDesc,
                    record.eventOverview,
                    record.eventCategory,
                    record.eventReports,
                    record.eventTime,
                    record.creatorId,
                    record.creatorName,
                    record.cost,
                    eventImageBase64
                );
            });
            
            return events;
        } catch (error) {
            console.error('Error getting events:', error);
            throw new Error("Error getting events");
        } finally {
            if (connection) {
                try {
                    await connection.close();
                } catch (closeError) {
                    console.error('Error closing the connection:', closeError);
                }
            }
        }
    }

    static async getEventById(eventId) {
        let connection;

        try {
            connection = await sql.connect(dbConfig);
            const sqlQuery = `
                SELECT 
                    e.eventId, 
                    e.eventName, 
                    e.eventDesc, 
                    e.eventOverview, 
                    e.eventCategory, 
                    e.eventReports, 
                    e.eventTime, 
                    e.creatorId, 
                    u.name AS creatorName,
                    e.cost,
                    e.eventImage
                FROM Events e
                JOIN Users u ON e.creatorId = u.userId
                WHERE e.eventId = @eventId
            `;
            const request = connection.request();
            request.input("eventId", sql.Int, eventId);
            const result = await request.query(sqlQuery);

            const record = result.recordset[0];
          
