    req.user = decoded;
    console.log('Token decoded:', decoded); // Debug log
    next();
  } catch (error) {
    console.log('Invalid token:', error); // Debug log
    res.status(400).json({
      message: 'Invalid token.'
    });
  }
};
module.exports = authenticateToken;
